          statusSelect.value = 'In Progress';
        } else if (type === 'completed') {
          statusSelect.value = mediaType === 'Movies' ? 'Watched' : 
                              (mediaType === 'Anime' || mediaType === 'TV Shows' ? 'Watched' : 'Read');
