function addMedia(mediaData) {
  var ss = SpreadsheetApp.getActiveSpreadsheet();
  var sheet = ss.getSheetByName(mediaData.type);

  if (!sheet) {
    throw new Error('Sheet not found: ' + mediaData.type + '. Please run Setup Sheets first.');
  }

  var row = [];
  var dateAdded = new Date().toLocaleDateString();

  // Track time if starting
  if (mediaData.status === 'In Progress') {
    PropertiesService.getUserProperties().setProperty(
      'startTime_' + mediaData.title, 
      new Date().getTime().toString()
    );
  }

  switch(mediaData.type) {
    case 'Anime':
    case 'TV Shows':
      row = [
        mediaData.title,
        mediaData.status,
        mediaData.progress || '',
        mediaData.season || '',
        mediaData.episode || '',
        mediaData.genre || '',
        dateAdded,
        mediaData.notes || ''
      ];
      break;

    case 'Manhwa':
    case 'Pornhwa':
    case 'Novels':
      row = [
        mediaData.title,
        mediaData.status,
        mediaData.progress || '',
        mediaData.chapter || '',
        mediaData.genre || '',
        dateAdded,
        mediaData.notes || ''
      ];
      break;

    case 'Movies':
      row = [
        mediaData.title,
        mediaData.status,
        mediaData.genre || '',
        dateAdded,
        mediaData.notes || ''
      ];
      break;
  }

  sheet.appendRow(row);

  // Check for achievements
  checkAchievements('add', mediaData);

  // Auto-archive if dropped
  if (mediaData.status === 'Dropped') {
    moveToArchive(sheet, sheet.getLastRow());
  }

  return true;
}

