import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertMediaItemSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Use a default user ID since we removed authentication
  const DEFAULT_USER_ID = "default-user";

  // Auth routes (simplified without authentication)
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      const user = await storage.getUser(DEFAULT_USER_ID);
      res.json(user || { id: DEFAULT_USER_ID, email: "user@example.com" });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Media routes
  app.get("/api/media", async (req: any, res) => {
    try {
      const { type, status } = req.query;
      const filters = {
        type: typeof type === 'string' ? type : undefined,
        status: typeof status === 'string' ? status : undefined
      };

      const mediaItems = await storage.getMediaItems(DEFAULT_USER_ID, filters);
      res.json(mediaItems);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch media items" });
    }
  });

  app.get("/api/media/:id", async (req: any, res) => {
    try {
      const mediaItem = await storage.getMediaItem(req.params.id, DEFAULT_USER_ID);
      if (!mediaItem) {
        return res.status(404).json({ error: "Media item not found" });
      }
      res.json(mediaItem);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch media item" });
    }
  });

  app.post("/api/media", async (req: any, res) => {
    try {
      const validatedData = insertMediaItemSchema.parse(req.body);
      const mediaItem = await storage.createMediaItem({
        ...validatedData,
        userId: DEFAULT_USER_ID
      });
      res.status(201).json(mediaItem);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid data", details: error.errors });
      }
      res.status(500).json({ error: "Failed to create media item" });
    }
  });

  app.patch("/api/media/:id", async (req: any, res) => {
    try {
      const updates = insertMediaItemSchema.partial().parse(req.body);
      const mediaItem = await storage.updateMediaItem(req.params.id, DEFAULT_USER_ID, updates);
      if (!mediaItem) {
        return res.status(404).json({ error: "Media item not found" });
      }
      res.json(mediaItem);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid data", details: error.errors });
      }
      res.status(500).json({ error: "Failed to update media item" });
    }
  });

  app.delete("/api/media/:id", async (req: any, res) => {
    try {
      const success = await storage.deleteMediaItem(req.params.id, DEFAULT_USER_ID);
      if (!success) {
        return res.status(404).json({ error: "Media item not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete media item" });
    }
  });

  // Progress routes
  app.get("/api/media/in-progress", async (req: any, res) => {
    try {
      const items = await storage.getInProgressItems(DEFAULT_USER_ID);
      res.json(items);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch in-progress items" });
    }
  });

  app.post("/api/media/:id/increment", async (req: any, res) => {
    try {
      const mediaItem = await storage.getMediaItem(req.params.id, DEFAULT_USER_ID);
      if (!mediaItem) {
        return res.status(404).json({ error: "Media item not found" });
      }

      let updates: any = {};

      if (mediaItem.type === 'Anime' || mediaItem.type === 'TV Shows') {
        const currentEpisode = mediaItem.episode || 0;
        const currentSeason = mediaItem.season || 1;
        const newEpisode = currentEpisode + 1;

        updates = {
          episode: newEpisode,
          season: currentSeason,
          progress: `S${currentSeason}E${newEpisode}`
        };
      } else {
        const currentChapter = mediaItem.chapter || 0;
        const newChapter = currentChapter + 1;

        updates = {
          chapter: newChapter,
          progress: `Ch${newChapter}`
        };
      }

      const updatedItem = await storage.updateMediaItem(req.params.id, DEFAULT_USER_ID, updates);
      res.json(updatedItem);
    } catch (error) {
      res.status(500).json({ error: "Failed to increment progress" });
    }
  });

  app.post("/api/media/:id/complete", async (req: any, res) => {
    try {
      const mediaItem = await storage.getMediaItem(req.params.id, DEFAULT_USER_ID);
      if (!mediaItem) {
        return res.status(404).json({ error: "Media item not found" });
      }

      const completedStatus = (mediaItem.type === 'Anime' || mediaItem.type === 'Movies' || mediaItem.type === 'TV Shows') 
        ? 'Watched' 
        : 'Read';

      const updatedItem = await storage.updateMediaItem(req.params.id, DEFAULT_USER_ID, {
        status: completedStatus,
        dateCompleted: new Date()
      });

      res.json(updatedItem);
    } catch (error) {
      res.status(500).json({ error: "Failed to mark item as complete" });
    }
  });

  // Stats routes
  app.get("/api/stats", async (req: any, res) => {
    try {
      const stats = await storage.getDetailedStats(DEFAULT_USER_ID);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch stats" });
    }
  });

  app.get("/api/stats/user", async (req: any, res) => {
    try {
      const userStats = await storage.getUserStats(DEFAULT_USER_ID);
      res.json(userStats);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch user stats" });
    }
  });

  // Search and random routes
  app.get("/api/search", async (req: any, res) => {
    try {
      const { q } = req.query;
      if (typeof q !== 'string' || q.trim().length === 0) {
        return res.status(400).json({ error: "Query parameter is required" });
      }

      const results = await storage.searchMediaItems(DEFAULT_USER_ID, q.trim());
      res.json(results);
    } catch (error) {
      res.status(500).json({ error: "Failed to search media items" });
    }
  });

  app.get("/api/random", async (req: any, res) => {
    try {
      const { type, status } = req.query;
      const filters = {
        type: typeof type === 'string' ? type : undefined,
        status: typeof status === 'string' ? status : undefined
      };

      const randomItem = await storage.getRandomItem(DEFAULT_USER_ID, filters);
      if (!randomItem) {
        return res.status(404).json({ error: "No items found with the specified filters" });
      }

      res.json(randomItem);
    } catch (error) {
      res.status(500).json({ error: "Failed to get random item" });
    }
  });

  // Achievement routes
  app.get("/api/achievements", async (req: any, res) => {
    try {
      const achievements = await storage.getUserAchievements(DEFAULT_USER_ID);
      res.json(achievements);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch achievements" });
    }
  });

  // Recent items
  app.get("/api/recent", async (req: any, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 10;
      const recentItems = await storage.getRecentlyAdded(DEFAULT_USER_ID, limit);
      res.json(recentItems);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch recent items" });
    }
  });

  // External media search with real APIs
  app.get("/api/search-external", async (req: any, res) => {
    try {
      const { query, type } = req.query;
      if (!query || !type) {
        return res.status(400).json({ error: "Query and type parameters are required" });
      }

      let results: any[] = [];

      if (type === 'Anime') {
        // AniList GraphQL API (no key needed)
        try {
          const graphqlQuery = `
            query ($search: String) {
              Page(perPage: 5) {
                media(search: $search, type: ANIME) {
                  id
                  title {
                    romaji
                    english
                  }
                  coverImage {
                    large
                  }
                  episodes
                  description
                  genres
                  startDate {
                    year
                  }
                }
              }
            }
          `;

          const response = await fetch('https://graphql.anilist.co', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              query: graphqlQuery,
              variables: { search: query }
            })
          });

          const data = await response.json();
          if (data.data?.Page?.media) {
            results = data.data.Page.media.map((item: any) => ({
              title: item.title.english || item.title.romaji,
              imageUrl: item.coverImage.large,
              description: item.description?.replace(/<[^>]*>/g, '').substring(0, 200) + '...',
              releaseYear: item.startDate?.year,
              genres: item.genres?.slice(0, 3) || [],
              externalId: `anilist_${item.id}`,
              episodes: item.episodes
            }));
          }
        } catch (error) {
          console.error('AniList API error:', error);
        }
      } else if (type === 'Movies' || type === 'TV Shows') {
        // TMDB API - fallback to mock if no API key
        const TMDB_KEY = process.env.TMDB_API_KEY;
        if (TMDB_KEY) {
          try {
            const mediaType = type === 'Movies' ? 'movie' : 'tv';
            const response = await fetch(
              `https://api.themoviedb.org/3/search/${mediaType}?api_key=${TMDB_KEY}&query=${encodeURIComponent(query)}`
            );
            const data = await response.json();

            if (data.results) {
              results = data.results.slice(0, 5).map((item: any) => ({
                title: item.title || item.name,
                imageUrl: item.poster_path ? `https://image.tmdb.org/t/p/w300${item.poster_path}` : null,
                description: item.overview,
                releaseYear: item.release_date ? parseInt(item.release_date.split('-')[0]) : 
                           item.first_air_date ? parseInt(item.first_air_date.split('-')[0]) : null,
                genres: [], // Would need additional API call for genres
                externalId: `tmdb_${item.id}`
              }));
            }
          } catch (error) {
            console.error('TMDB API error:', error);
          }
        }
      }

      // Fallback to basic search if no results from APIs
      if (results.length === 0) {
        results = [{
          title: query,
          imageUrl: null,
          description: `Search result for "${query}". Add TMDB_API_KEY to environment for enhanced ${type} search.`,
          releaseYear: new Date().getFullYear(),
          genres: [],
          externalId: `manual_${Date.now()}`
        }];
      }

      res.json(results);
    } catch (error) {
      console.error("External search error:", error);
      res.status(500).json({ error: "Failed to search external databases" });
    }
  });

  // Bulk operations
  app.patch("/api/bulk/update", async (req: any, res) => {
    try {
      const { ids, updates } = req.body;

      if (!Array.isArray(ids) || ids.length === 0) {
        return res.status(400).json({ error: 'IDs array is required' });
      }

      if (!updates || typeof updates !== 'object') {
        return res.status(400).json({ error: 'Updates object is required' });
      }

      const results = [];
      for (const id of ids) {
        try {
          const item = await storage.getMediaItem(id, DEFAULT_USER_ID);
          if (item) {
            const updatedItem = await storage.updateMediaItem(id, DEFAULT_USER_ID, updates);
            results.push(updatedItem);
          }
        } catch (error) {
          console.error(`Failed to update item ${id}:`, error);
        }
      }

      res.json({ updated: results.length, items: results });
    } catch (error) {
      console.error('Bulk update error:', error);
      res.status(500).json({ error: 'Failed to update items' });
    }
  });

  app.delete("/api/bulk/delete", async (req: any, res) => {
    try {
      const { ids } = req.body;

      if (!Array.isArray(ids) || ids.length === 0) {
        return res.status(400).json({ error: 'IDs array is required' });
      }

      let deletedCount = 0;
      for (const id of ids) {
        try {
          await storage.deleteMediaItem(id, DEFAULT_USER_ID);
          deletedCount++;
        } catch (error) {
          console.error(`Failed to delete item ${id}:`, error);
        }
      }

      res.json({ deleted: deletedCount });
    } catch (error) {
      console.error('Bulk delete error:', error);
      res.status(500).json({ error: 'Failed to delete items' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  mediaItems, 
  achievements, 
  userStats,
  type User, 
  type UpsertUser,
  type MediaItem,
  type InsertMediaItem,
  type Achievement,
  type InsertAchievement,
  type UserStats,
  type InsertUserStats
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, and, count, sql } from "drizzle-orm";

export interface IStorage {
  // User methods
  // (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Media methods
  getMediaItems(userId: string, filters?: { type?: string; status?: string }): Promise<MediaItem[]>;
  getMediaItem(id: string, userId: string): Promise<MediaItem | undefined>;
  createMediaItem(mediaItem: InsertMediaItem & { userId: string }): Promise<MediaItem>;
  updateMediaItem(id: string, userId: string, updates: Partial<InsertMediaItem>): Promise<MediaItem | undefined>;
  deleteMediaItem(id: string, userId: string): Promise<boolean>;
  getInProgressItems(userId: string): Promise<MediaItem[]>;
  getRecentlyAdded(userId: string, limit?: number): Promise<MediaItem[]>;
  searchMediaItems(userId: string, query: string): Promise<MediaItem[]>;
  getRandomItem(userId: string, filters?: { type?: string; status?: string }): Promise<MediaItem | undefined>;

  // Stats methods
  getUserStats(userId: string): Promise<UserStats | undefined>;
  updateUserStats(userId: string, updates: Partial<InsertUserStats>): Promise<UserStats>;
  getDetailedStats(userId: string): Promise<any>;

  // Achievement methods
  getUserAchievements(userId: string): Promise<Achievement[]>;
  createAchievement(achievement: InsertAchievement & { userId: string }): Promise<Achievement>;
  checkAndUnlockAchievements(userId: string): Promise<Achievement[]>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();

    // Create initial user stats if user is new
    await db.insert(userStats).values({ userId: user.id }).onConflictDoNothing();

    return user;
  }

  // Media methods
  async getMediaItems(userId: string, filters?: { type?: string; status?: string }): Promise<MediaItem[]> {
    let query = db.select().from(mediaItems).where(
      and(
        eq(mediaItems.userId, userId),
        eq(mediaItems.isArchived, false)
      )
    );

    if (filters?.type && filters.type !== 'all') {
      query = db.select().from(mediaItems).where(
        and(
          eq(mediaItems.userId, userId),
          eq(mediaItems.type, filters.type),
          eq(mediaItems.isArchived, false)
        )
      );
    }

    if (filters?.status && filters.status !== 'all') {
      const statusFilter = filters.status === 'planned' 
        ? ['To Watch', 'To Read']
        : filters.status === 'inprogress' 
        ? ['In Progress']
        : [filters.status];

      query = db.select().from(mediaItems).where(
        and(
          eq(mediaItems.userId, userId),
          sql`${mediaItems.status} = ANY(${statusFilter})`,
          eq(mediaItems.isArchived, false),
          ...(filters.type && filters.type !== 'all' ? [eq(mediaItems.type, filters.type)] : [])
        )
      );
    }

    return await query.orderBy(desc(mediaItems.dateAdded));
  }

  async getMediaItem(id: string, userId: string): Promise<MediaItem | undefined> {
    const [item] = await db
      .select()
      .from(mediaItems)
      .where(and(eq(mediaItems.id, id), eq(mediaItems.userId, userId)));
    return item || undefined;
  }

  async createMediaItem(mediaItem: InsertMediaItem & { userId: string }): Promise<MediaItem> {
    const [item] = await db
      .insert(mediaItems)
      .values(mediaItem)
      .returning();

    // Update user stats
    await this.updateStatsAfterAction(mediaItem.userId, 'add', mediaItem.status);

    // Check for achievements
    await this.checkAndUnlockAchievements(mediaItem.userId);

    return item;
  }

  async updateMediaItem(id: string, userId: string, updates: Partial<InsertMediaItem>): Promise<MediaItem | undefined> {
    const [item] = await db
      .update(mediaItems)
      .set(updates)
      .where(and(eq(mediaItems.id, id), eq(mediaItems.userId, userId)))
      .returning();

    if (item) {
      await this.updateStatsAfterAction(userId, 'update', item.status);
      await this.checkAndUnlockAchievements(userId);
    }

    return item || undefined;
  }

  async deleteMediaItem(id: string, userId: string): Promise<boolean> {
    const result = await db
      .delete(mediaItems)
      .where(and(eq(mediaItems.id, id), eq(mediaItems.userId, userId)));

    if (result.rowCount && result.rowCount > 0) {
      await this.updateStatsAfterAction(userId, 'delete');
      return true;
    }
    return false;
  }

  async getInProgressItems(userId: string): Promise<MediaItem[]> {
    return await db
      .select()
      .from(mediaItems)
      .where(
        and(
          eq(mediaItems.userId, userId),
          eq(mediaItems.status, 'In Progress'),
          eq(mediaItems.isArchived, false)
        )
      )
      .orderBy(desc(mediaItems.dateAdded));
  }

  async getRecentlyAdded(userId: string, limit = 10): Promise<MediaItem[]> {
    return await db
      .select()
      .from(mediaItems)
      .where(and(eq(mediaItems.userId, userId), eq(mediaItems.isArchived, false)))
      .orderBy(desc(mediaItems.dateAdded))
      .limit(limit);
  }

  async searchMediaItems(userId: string, query: string): Promise<MediaItem[]> {
    return await db
      .select()
      .from(mediaItems)
      .where(
        and(
          eq(mediaItems.userId, userId),
          sql`${mediaItems.title} ILIKE ${`%${query}%`}`,
          eq(mediaItems.isArchived, false)
        )
      )
      .orderBy(desc(mediaItems.dateAdded));
  }

  async getRandomItem(userId: string, filters?: { type?: string; status?: string }): Promise<MediaItem | undefined> {
    const items = await this.getMediaItems(userId, filters);
    if (items.length === 0) return undefined;

    const randomIndex = Math.floor(Math.random() * items.length);
    return items[randomIndex];
  }

  // Stats methods
  async getUserStats(userId: string): Promise<UserStats | undefined> {
    const [stats] = await db
      .select()
      .from(userStats)
      .where(eq(userStats.userId, userId));
    return stats || undefined;
  }

  async updateUserStats(userId: string, updates: Partial<InsertUserStats>): Promise<UserStats> {
    const [stats] = await db
      .update(userStats)
      .set({ ...updates, lastActivity: new Date() })
      .where(eq(userStats.userId, userId))
      .returning();
    return stats;
  }

  async getDetailedStats(userId: string): Promise<any> {
    const items = await db
      .select()
      .from(mediaItems)
      .where(and(eq(mediaItems.userId, userId), eq(mediaItems.isArchived, false)));

    const stats = {
      total: items.length,
      byStatus: {} as Record<string, number>,
      byType: {} as Record<string, any>,
      byGenre: {} as Record<string, number>,
      topGenres: [] as any[],
      inProgressCount: 0,
      completedCount: 0,
      recentlyAdded: [] as MediaItem[]
    };

    items.forEach(item => {
      // Status stats
      stats.byStatus[item.status] = (stats.byStatus[item.status] || 0) + 1;

      // Type stats
      if (!stats.byType[item.type]) {
        stats.byType[item.type] = { total: 0, completed: 0, inProgress: 0, planned: 0, dropped: 0 };
      }
      stats.byType[item.type].total++;

      if (item.status === 'Watched' || item.status === 'Read') {
        stats.byType[item.type].completed++;
        stats.completedCount++;
      } else if (item.status === 'In Progress') {
        stats.byType[item.type].inProgress++;
        stats.inProgressCount++;
      } else if (item.status === 'To Watch' || item.status === 'To Read') {
        stats.byType[item.type].planned++;
      } else if (item.status === 'Dropped') {
        stats.byType[item.type].dropped++;
      }

      // Genre stats
      if (item.genre) {
        stats.byGenre[item.genre] = (stats.byGenre[item.genre] || 0) + 1;
      }
    });

    // Convert genres to sorted array
    stats.topGenres = Object.keys(stats.byGenre)
      .map(genre => ({ name: genre, count: stats.byGenre[genre] }))
      .sort((a, b) => b.count - a.count);

    // Get recently added
    stats.recentlyAdded = await this.getRecentlyAdded(userId, 5);

    return stats;
  }

  // Achievement methods
  async getUserAchievements(userId: string): Promise<Achievement[]> {
    return await db
      .select()
      .from(achievements)
      .where(eq(achievements.userId, userId))
      .orderBy(desc(achievements.unlockedAt));
  }

  async createAchievement(achievement: InsertAchievement & { userId: string }): Promise<Achievement> {
    const [newAchievement] = await db
      .insert(achievements)
      .values(achievement)
      .returning();
    return newAchievement;
  }

  async checkAndUnlockAchievements(userId: string): Promise<Achievement[]> {
    const stats = await this.getDetailedStats(userId);
    const existingAchievements = await this.getUserAchievements(userId);
    const existingTypes = existingAchievements.map(a => a.type);
    const newAchievements: Achievement[] = [];

    // Collector achievements
    if (stats.total >= 10 && !existingTypes.includes('collector_10')) {
      const achievement = await this.createAchievement({
        userId,
        type: 'collector_10',
        title: 'Getting Started',
        description: 'Added your first 10 items',
        metadata: { count: stats.total }
      });
      newAchievements.push(achievement);
    }

    if (stats.total >= 50 && !existingTypes.includes('collector_50')) {
      const achievement = await this.createAchievement({
        userId,
        type: 'collector_50',
        title: 'Collector',
        description: 'Added 50 items to your library',
        metadata: { count: stats.total }
      });
      newAchievements.push(achievement);
    }

    if (stats.total >= 100 && !existingTypes.includes('collector_100')) {
      const achievement = await this.createAchievement({
        userId,
        type: 'collector_100',
        title: 'Curator',
        description: 'Added 100 items to your library',
        metadata: { count: stats.total }
      });
      newAchievements.push(achievement);
    }

    // Completion achievements
    if (stats.completedCount >= 10 && !existingTypes.includes('completed_10')) {
      const achievement = await this.createAchievement({
        userId,
        type: 'completed_10',
        title: 'Finisher',
        description: 'Completed your first 10 items',
        metadata: { count: stats.completedCount }
      });
      newAchievements.push(achievement);
    }

    return newAchievements;
  }

  private async updateStatsAfterAction(userId: string, action: 'add' | 'update' | 'delete', status?: string): Promise<void> {
    const stats = await this.getDetailedStats(userId);

    await this.updateUserStats(userId, {
      totalItems: stats.total,
      completedItems: stats.completedCount,
      inProgressItems: stats.inProgressCount,
      plannedItems: (stats.byStatus['To Watch'] || 0) + (stats.byStatus['To Read'] || 0),
      droppedItems: stats.byStatus['Dropped'] || 0,
    });
  }
}

export const storage = new DatabaseStorage();

