  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(353, 68%, 32%);  /* #7A1927 */
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(353, 68%, 32%);
  --radius: 0.75rem;

  /* Custom media tracker colors - exact match to your specs */
  --surface: hsl(0, 0%, 8%);      /* #141414 */
  --surface-2: hsl(0, 0%, 12%);   /* #1f1f1f */
  --surface-3: hsl(0, 0%, 16%);   /* #292929 */
  --accent-red: hsl(353, 68%, 32%);    /* #7A1927 primary red */
  --primary-red: hsl(353, 68%, 35%);   /* slightly lighter */
  --red-hover: hsl(353, 68%, 42%);     /* hover state */
  --red-muted: hsl(353, 50%, 28%);     /* muted variant */
  --red-light: hsl(353, 60%, 55%);     /* light variant */
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(346, 63%, 32%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background: #0a0a0a !important;
    font-family: 'Inter', system-ui, sans-serif;
    overflow-x: hidden;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: #0a0a0a;
  }

  ::-webkit-scrollbar-thumb {
    background: #7A1927;
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: #9d2332;
  }
}

@layer components {
  .gradient-border {
    background: linear-gradient(90deg, #7A1927, #ff6b6b);
    border-radius: 12px;
    padding: 1px;
  }

  .gradient-border > div {
    background: #1a1a1a;
    border-radius: 11px;
  }

  .progress-bar {
    background: linear-gradient(90deg, #7A1927, #ff6b6b);
  }

  .hover-scale {
    transition: transform 0.2s ease;
  }

  .hover-scale:hover {
    transform: scale(1.02);
  }

  .sidebar-item {
    transition: all 0.2s ease;
  }

  .sidebar-item:hover {
    background: #2a2a2a;
    border-left: 4px solid #7A1927;
  }

  .modal-backdrop {
    backdrop-filter: blur(8px);
  }

  .bg-surface {
    background-color: var(--surface);
  }

  .bg-surface-2 {
    background-color: var(--surface-2);
  }

  .bg-surface-3 {
    background-color: var(--surface-3);
  }

  .text-accent {
    color: var(--accent-red);
  }

  .bg-primary-red {
    background-color: var(--primary-red);
  }

  .border-gray-700 {
    border-color: hsl(240, 3.7%, 15.9%);
  }

  /* Line clamp utilities */
  .line-clamp-1 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 1;
  }

  .line-clamp-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  .line-clamp-3 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 3;
  }
}

