import { Trophy, Flame } from "lucide-react";
import type { Achievement } from "@shared/schema";

interface AchievementWidgetProps {
  achievements?: Achievement[];
  isLoading?: boolean;
}

const getAchievementIcon = (type: string) => {
  switch (type) {
    case 'collector_10':
    case 'collector_50':
    case 'collector_100': return Trophy;
    case 'completed_10':
    case 'completed_50': return Trophy;
    case 'week_streak': return Flame;
    default: return Trophy;
  }
};

const getAchievementColor = (type: string) => {
  switch (type) {
    case 'collector_10': return 'bg-bronze-500/20 text-bronze-400';
    case 'collector_50': return 'bg-silver-500/20 text-silver-400';
    case 'collector_100': return 'bg-yellow-500/20 text-yellow-400';
    case 'week_streak': return 'bg-blue-500/20 text-blue-400';
    default: return 'bg-yellow-500/20 text-yellow-400';
  }
};

const formatDate = (dateString: string) => {
  const date = new Date(dateString);
  const now = new Date();
  const diffTime = Math.abs(now.getTime() - date.getTime());
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  if (diffDays === 1) return '1 day ago';
  if (diffDays < 7) return `${diffDays} days ago`;
  if (diffDays < 30) return `${Math.ceil(diffDays / 7)} weeks ago`;
  return `${Math.ceil(diffDays / 30)} months ago`;
};

export default function AchievementWidget({ achievements = [], isLoading }: AchievementWidgetProps) {
  if (isLoading) {
    return (
      <div className="bg-surface rounded-xl p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-bold">üèÜ Achievements</h3>
        </div>
        <div className="space-y-3">
          {[...Array(2)].map((_, i) => (
            <div key={i} className="flex items-center space-x-3 p-3 bg-surface-2 rounded-lg animate-pulse">
              <div className="w-8 h-8 bg-gray-600 rounded-full"></div>
              <div className="flex-1 space-y-2">
                <div className="h-4 bg-gray-600 rounded w-3/4"></div>
                <div className="h-3 bg-gray-700 rounded w-1/2"></div>
              </div>
              <div className="h-3 bg-gray-700 rounded w-16"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  const recentAchievements = achievements.slice(0, 3);

  return (
    <div className="bg-surface rounded-xl p-6 border border-gray-700">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-bold">üèÜ Achievements</h3>
        <a href="#" className="text-primary-red hover:text-accent text-sm">View All</a>
      </div>

      <div className="space-y-3">
        {recentAchievements.length === 0 ? (
          <div className="text-center py-8">
            <Trophy className="w-12 h-12 text-gray-500 mx-auto mb-4" />
            <p className="text-gray-400">No achievements yet</p>
            <p className="text-sm text-gray-500 mt-2">
              Start adding and completing media to unlock achievements!
            </p>
          </div>
        ) : (
          recentAchievements.map((achievement) => {
            const Icon = getAchievementIcon(achievement.type);
            return (
              <div key={achievement.id} className="flex items-center space-x-3 p-3 bg-surface-2 rounded-lg">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${getAchievementColor(achievement.type)}`}>
                  <Icon className="w-4 h-4" />
                </div>
                <div className="flex-1">
                  <p className="text-sm font-medium">{achievement.title}</p>
                  <p className="text-xs text-gray-400">{achievement.description}</p>
                </div>
                <span className="text-xs text-gray-500">
                  {achievement.unlockedAt ? formatDate(achievement.unlockedAt.toString()) : 'Recently'}
                </span>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

client/src/components/AddMediaModal.tsx.content:
import { useState, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Search, Tags, X } from "lucide-react";
import MediaSearch from "@/components/MediaSearch";
import GenreSelector from "@/components/GenreSelector";
import type { InsertMediaItem } from "@shared/schema";

interface AddMediaModalProps {
  isOpen: boolean;
  onClose: () => void;
}

// Media types based on your Google Sheets specifications
const mediaTypes = ['Anime', 'Manhwa', 'Pornhwa', 'Novels', 'Movies', 'TV Shows'];
const genres = [
  'Fantasy', 'Sci-Fi', 'Romance', 'Slice of Life', 'Action', 'Adventure',
  'Comedy', 'Drama', 'Horror', 'Mystery', 'Thriller', 'Psychological',
  'Sports', 'Supernatural', 'Historical'
];

export default function AddMediaModal({ isOpen, onClose }: AddMediaModalProps) {
  const [formData, setFormData] = useState<Partial<InsertMediaItem>>({
    title: '',
    type: '',
    status: '',
    genre: '',
    notes: '',
    season: 1,
    episode: 0,
    chapter: 0,
    imageUrl: '',
    description: '',
    externalId: '',
    releaseYear: undefined,
  });

  const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isGenreModalOpen, setIsGenreModalOpen] = useState(false);
  const [showProgressFields, setShowProgressFields] = useState(false);
  const [autoSearchResults, setAutoSearchResults] = useState<any[]>([]);
  const [showAutoSearch, setShowAutoSearch] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const addMediaMutation = useMutation({
    mutationFn: async (data: InsertMediaItem) => {
      const response = await apiRequest('POST', '/api/media', data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Media added successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
      queryClient.invalidateQueries({ queryKey: ['/api/recent'] });
      queryClient.invalidateQueries({ queryKey: ['/api/media'] });
      onClose();
      resetForm();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to add media. Please try again.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      title: '',
      type: '',
      status: '',
      genre: '',
      notes: '',
      season: 1,
      episode: 0,
      chapter: 0,
      imageUrl: '',
      description: '',
      externalId: '',
      releaseYear: undefined,
    });
    setSelectedGenres([]);
    setShowProgressFields(false);
    setAutoSearchResults([]);
    setShowAutoSearch(false);
  };

  // Auto search when title is 3+ characters
  useEffect(() => {
    const performAutoSearch = async () => {
      if (formData.title && formData.title.length >= 3 && formData.type) {
        try {
          const response = await fetch(`/api/external-search?query=${encodeURIComponent(formData.title)}&type=${encodeURIComponent(formData.type)}`);
          const results = await response.json();
          setAutoSearchResults(results.slice(0, 5)); // Show max 5 results
          setShowAutoSearch(results.length > 0);
        } catch (error) {
          console.error('Auto search error:', error);
          setShowAutoSearch(false);
        }
      } else {
        setShowAutoSearch(false);
      }
    };

    const timeoutId = setTimeout(performAutoSearch, 300); // Debounce
    return () => clearTimeout(timeoutId);
  }, [formData.title, formData.type]);

  // Show progress fields when status is selected and it's "In Progress"
  useEffect(() => {
    if (formData.status === 'In Progress') {
      setShowProgressFields(true);
    } else {
      setShowProgressFields(false);
    }
  }, [formData.status]);



  const handleGenresChange = (genres: string[]) => {
    setSelectedGenres(genres);
    setFormData(prev => ({
      ...prev,
      genre: genres.join(', ')
    }));
  };

  const getStatusOptions = (type: string) => {
    if (type === 'Movies') {
      return ['To Watch', 'Watched', 'Dropped'];
    } else if (type === 'Anime' || type === 'TV Shows') {
      return ['To Watch', 'In Progress', 'Watched', 'Dropped'];
    } else if (type === 'Manhwa' || type === 'Pornhwa' || type === 'Novels') {
      return ['To Read', 'In Progress', 'Read', 'Dropped'];
    }
    return [];
  };

  const setQuickStatus = (statusType: 'planned' | 'progress' | 'completed') => {
    if (!formData.type) return;

    let status = '';
    if (statusType === 'planned') {
      status = (['Movies', 'Anime', 'TV Shows'].includes(formData.type || '')) ? 'To Watch' : 'To Read';
    } else if (statusType === 'progress') {
      status = 'In Progress';
    } else if (statusType === 'completed') {
      status = (['Movies', 'Anime', 'TV Shows'].includes(formData.type || '')) ? 'Watched' : 'Read';
    }

    setFormData(prev => ({ ...prev, status }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.title || !formData.type || !formData.status) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }

    // Format progress
    let progress = '';
    if (formData.type === 'Anime' || formData.type === 'TV Shows') {
      if (formData.season && formData.episode) {
        progress = `S${formData.season}E${formData.episode}`;
      }
    } else if (['Manhwa', 'Pornhwa', 'Novels'].includes(formData.type || '')) {
      if (formData.chapter) {
        progress = `Ch${formData.chapter}`;
      }
    }

    const submitData: InsertMediaItem = {
      title: formData.title!,
      type: formData.type!,
      status: formData.status!,
      progress,
      season: formData.season,
      episode: formData.episode,
      chapter: formData.chapter,
      genre: selectedGenres.length > 0 ? selectedGenres.join(', ') : null,
      notes: formData.notes || null,
      rating: null,
      dateCompleted: null,
      timeSpent: null,
      isArchived: false,
      imageUrl: formData.imageUrl || null,
      description: formData.description || null,
      externalId: formData.externalId || null,
      releaseYear: formData.releaseYear || null,
    };

    addMediaMutation.mutate(submitData);
  };

  // Handle auto-search result selection
  const handleSearchResult = (result: any) => {
    setFormData(prev => ({
      ...prev,
      title: result.title,
      imageUrl: result.imageUrl || '',
      description: result.description || '',
      releaseYear: result.releaseYear || undefined,
      externalId: result.externalId || ''
    }));

    // Set first genre if available
    if (result.genres && result.genres.length > 0) {
      setSelectedGenres([result.genres[0]]);
    }

    setShowAutoSearch(false);
    setIsSearchOpen(false);
  };

  // Auto-search when typing 3+ characters
  useEffect(() => {
    if (formData.title && formData.title.length >= 3 && formData.type) {
      const timer = setTimeout(async () => {
        try {
          const response = await fetch(`/api/search-external?query=${encodeURIComponent(formData.title!)}&type=${encodeURIComponent(formData.type!)}`);
          const results = await response.json();
          setAutoSearchResults(results);
          setShowAutoSearch(results.length > 0);
        } catch (error) {
          console.error('Auto-search error:', error);
          setShowAutoSearch(false);
        }
      }, 500);

      return () => clearTimeout(timer);
    } else {
      setShowAutoSearch(false);
    }
  }, [formData.title, formData.type]);

  const showSeasonField = formData.type === 'Anime' || formData.type === 'TV Shows';
  const episodeLabel = ['Manhwa', 'Pornhwa', 'Novels'].includes(formData.type || '') ? 'Chapter' : 'Episode';

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="bg-surface border-gray-700 max-w-md max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-xl font-bold">‚ûï Add New Media</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Media Type */}
          <div>
            <Label htmlFor="type">Media Type*</Label>
            <Select value={formData.type} onValueChange={(value) => setFormData(prev => ({ ...prev, type: value, status: '' }))}>
              <SelectTrigger className="bg-surface-2 border-gray-600">
                <SelectValue placeholder="Select Type" />
              </SelectTrigger>
              <SelectContent className="bg-surface-2 border-gray-600">
                {mediaTypes.map((type: string) => (
                  <SelectItem key={type} value={type}>{type}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Title with Search Button */}
          <div>
            <Label htmlFor="title">Title*</Label>
            <div className="flex space-x-2">
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                className="bg-surface-2 border-gray-600 flex-1"
                placeholder="Enter media title"
              />
              {formData.type && (
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsSearchOpen(true)}
                  className="bg-[#7A1927] hover:bg-[#9d2332] border-[#7A1927] text-white"
                >
                  <Search className="w-4 h-4" />
                </Button>
              )}
            </div>
            {formData.type && (
              <p className="text-xs text-gray-400 mt-1">
                Click the search button to find {formData.type.toLowerCase()} from external databases
              </p>
            )}

            {/* Auto Search Results */}
            {showAutoSearch && (
              <div className="bg-surface-2 border border-gray-600 rounded-md mt-2 max-h-40 overflow-y-auto">
                <div className="p-2 text-xs text-gray-400 border-b border-gray-600">
                  Auto-search results (click to fill):
                </div>
                {autoSearchResults.map((result, index) => (
                  <div
                    key={index}
                    className="p-2 hover:bg-surface-1 cursor-pointer border-b border-gray-700 last:border-b-0"
                    onClick={() => handleSearchResult(result)}
                  >
                    <div className="text-sm font-medium">{result.title}</div>
                    {result.releaseYear && (
                      <div className="text-xs text-gray-400">{result.releaseYear}</div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Status - only show if type is selected */}
          {formData.type && (
            <div>
              <Label htmlFor="status">Status*</Label>
              <Select value={formData.status} onValueChange={(value) => setFormData(prev => ({ ...prev, status: value }))}>
                <SelectTrigger className="bg-surface-2 border-gray-600">
                  <SelectValue placeholder="Select Status" />
                </SelectTrigger>
                <SelectContent className="bg-surface-2 border-gray-600">
                  {getStatusOptions(formData.type || '').map(status => (
                    <SelectItem key={status} value={status}>{status}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

            {/* Quick Status Buttons */}
            {formData.type && (
              <div className="flex space-x-2 mt-2">
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => setQuickStatus('planned')}
                  className="flex-1 bg-amber-500/20 text-amber-400 border-amber-500/30 hover:bg-amber-500/30"
                >
                  Plan to {formData.type === 'Movies' || formData.type === 'Anime' || formData.type === 'TV Shows' ? 'Watch' : 'Read'}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => setQuickStatus('progress')}
                  className="flex-1 bg-green-500/20 text-green-400 border-green-500/30 hover:bg-green-500/30"
                >
                  In Progress
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => setQuickStatus('completed')}
                  className="flex-1 bg-blue-500/20 text-blue-400 border-blue-500/30 hover:bg-blue-500/30"
                >
                  Completed
                </Button>
              </div>
            )}
          </div>
          )}

          {/* Progress Fields - only show if status is selected and status is "In Progress" */}
          {formData.status === 'In Progress' && (
            <div className="space-y-4">
              {showSeasonField && (
                <div>
                  <Label htmlFor="season">Season</Label>
                  <Input
                    id="season"
                    type="number"
                    min="1"
                    value={formData.season || ''}
                    onChange={(e) => setFormData(prev => ({ ...prev, season: parseInt(e.target.value) || 1 }))}
                    className="bg-surface-2 border-gray-600"
                  />
                </div>
              )}

              <div>
                <Label htmlFor="episode">{episodeLabel}</Label>
                <Input
                  id="episode"
                  type="number"
                  min="0"
                  value={showSeasonField ? (formData.episode || '') : (formData.chapter || '')}
                  onChange={(e) => {
                    const value = parseInt(e.target.value) || 0;
                    if (showSeasonField) {
                      setFormData(prev => ({ ...prev, episode: value }));
                    } else {
                      setFormData(prev => ({ ...prev, chapter: value }));
                    }
                  }}
                  className="bg-surface-2 border-gray-600"
                />
              </div>
            </div>
          )}

          {/* Genres (Multi-select) */}
          <div>
            <Label htmlFor="genre">Genres</Label>
            <div className="space-y-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsGenreModalOpen(true)}
                className="w-full bg-surface-2 border-gray-600 justify-start text-left"
              >
                <Tags className="w-4 h-4 mr-2" />
                {selectedGenres.length > 0 
                  ? `${selectedGenres.length} genre${selectedGenres.length > 1 ? 's' : ''} selected`
                  : 'Select genres...'
                }
              </Button>

              {selectedGenres.length > 0 && (
                <div className="flex flex-wrap gap-1">
                  {selectedGenres.map((genre) => (
                    <Badge 
                      key={genre} 
                      variant="secondary" 
                      className="bg-[#7A1927] text-white"
                    >
                      {genre}
                      <X 
                        className="w-3 h-3 ml-1 cursor-pointer" 
                        onClick={() => {
                          const newGenres = selectedGenres.filter(g => g !== genre);
                          setSelectedGenres(newGenres);
                          setFormData(prev => ({ ...prev, genre: newGenres.join(', ') }));
                        }}
                      />
                    </Badge>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Description (from search) */}
          {formData.description && (
            <div>
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                className="bg-surface-2 border-gray-600"
                rows={3}
                placeholder="Media description..."
              />
            </div>
          )}

          {/* Notes */}
          <div>
            <Label htmlFor="notes">Notes (Optional)</Label>
            <Textarea
              id="notes"
              value={formData.notes || ''}
              onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
              className="bg-surface-2 border-gray-600"
              placeholder="Any additional notes..."
              rows={3}
            />
          </div>

          {/* Submit Buttons */}
          <div className="flex space-x-3 pt-4">
            <Button 
              type="button" 
              variant="outline" 
              onClick={onClose} 
              className="flex-1 bg-gray-600 hover:bg-gray-700 border-gray-600"
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={addMediaMutation.isPending}
              className="flex-1 bg-primary-red hover:bg-primary-red/90"
            >
              {addMediaMutation.isPending ? 'Adding...' : 'Add Media'}
            </Button>
          </div>
        </form>

        {/* Search Modal */}
        <MediaSearch
          isOpen={isSearchOpen}
          onClose={() => setIsSearchOpen(false)}
          mediaType={formData.type || ''}
          onSelect={handleSearchResult}
        />

        {/* Genre Selection Modal */}
        <GenreSelector
          isOpen={isGenreModalOpen}
          onClose={() => setIsGenreModalOpen(false)}
          selectedGenres={selectedGenres}
          onGenresChange={handleGenresChange}
        />
      </DialogContent>
    </Dialog>
  );
}

client/src/components/CurrentlyActive.ts.content:
import { Plus, MoreHorizontal } from "lucide-react";
import { Button } from "@/components/ui/button";
import type { MediaItem } from "@shared/schema";

interface CurrentlyActiveProps {
  items?: MediaItem[];
  isLoading?: boolean;
  onQuickUpdate: () => void;
}

export default function CurrentlyActive({ items = [], isLoading, onQuickUpdate }: CurrentlyActiveProps) {
  if (isLoading) {
    return (
      <div className="bg-surface rounded-xl p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-bold">Currently Active</h3>
        </div>
        <div className="space-y-4">
          {[...Array(2)].map((_, i) => (
            <div key={i} className="border border-gray-600 rounded-lg p-4 animate-pulse">
              <div className="space-y-3">
                <div className="h-4 bg-gray-600 rounded w-3/4"></div>
                <div className="h-3 bg-gray-700 rounded w-1/2"></div>
                <div className="h-2 bg-gray-700 rounded w-full"></div>
                <div className="h-8 bg-gray-600 rounded"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Calculate progress percentage (dummy calculation for demo)
  const getProgressPercentage = (item: MediaItem) => {
    if (item.type === 'Anime' || item.type === 'TV Shows') {
      // Assume 24 episodes per season for calculation
      const totalEpisodes = (item.season || 1) * 24;
      const currentEpisode = item.episode || 0;
      return Math.min(Math.round((currentEpisode / totalEpisodes) * 100), 100);
    } else {
      // For manga/novels, assume arbitrary total for demo
      const currentChapter = item.chapter || 0;
      const estimatedTotal = Math.max(currentChapter * 1.5, 100); // Rough estimate
      return Math.min(Math.round((currentChapter / estimatedTotal) * 100), 90);
    }
  };

  return (
    <div className="bg-surface rounded-xl p-6 border border-gray-700">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-bold">Currently Active</h3>
        <Button 
          onClick={onQuickUpdate}
          variant="ghost" 
          size="sm"
          className="text-primary-red hover:text-accent"
        >
          <Plus className="w-4 h-4 mr-1" /> Quick Update
        </Button>
      </div>

      <div className="space-y-4">
        {items.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-400">No items in progress</p>
            <p className="text-sm text-gray-500 mt-2">
              Start watching or reading something to see it here!
            </p>
          </div>
        ) : (
          items.slice(0, 3).map((item) => {
            const progressPercent = getProgressPercentage(item);
            return (
              <div key={item.id} className="border border-gray-600 rounded-lg p-4">
                <div className="flex items-start justify-between mb-3">
                  <div>
                    <h4 className="font-medium text-sm">{item.title}</h4>
                    <p className="text-xs text-gray-400">{item.type}</p>
                  </div>
                  <span className="text-xs text-green-400">
                    {item.progress || 'No progress'}
                  </span>
                </div>

                {/* Progress Bar */}
                <div className="mb-3">
                  <div className="flex justify-between text-xs text-gray-400 mb-1">
                    <span>Progress</span>
                    <span>{progressPercent}%</span>
                  </div>
                  <div className="w-full bg-gray-700 rounded-full h-2">
                    <div 
                      className="progress-bar h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${progressPercent}%` }}
                    ></div>
                  </div>
                </div>

                <div className="flex space-x-2">
                  <Button
                    size="sm"
                    className="flex-1 bg-primary-red hover:bg-primary-red/90 text-xs"
                  >
                    <Plus className="w-3 h-3 mr-1" />
                    +1 {item.type === 'Anime' || item.type === 'TV Shows' ? 'Episode' : 'Chapter'}
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    className="bg-surface-2 hover:bg-surface-3 border-gray-600 text-xs"
                  >
                    <MoreHorizontal className="w-3 h-3" />
                  </Button>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

